import { useState,useEffect } from "react";
import BlogList  from "./BlogList";
const Home = () => {
   
  const [blogs,setBlogs] =useState([
      {
         ad:"Yeni Başlayanlar İçin C#",
         aciklama:"lorem ipsum",
         yazar:"luffy",
         id:1
      },
      {
         ad:"React öğreniyorum",
         aciklama:"lorem ipsum",
         yazar:"zorro",
         id:2
      },
      {
         ad:"İleri Seviye c#",
         aciklama:"lorem ipsum",
         yazar:"sanji",
         id:3
      },
      {
         ad:"İleri Seviye f#",
         aciklama:"lorem ipsum",
         yazar:"aliveli",
         id:4
      }
   ])

   const[isim,setIsim]=useState('luffy')
   

    useEffect(()=>{
      console.log("use effect çalıştı");
      console.log(blogs)
    },[blogs,isim])


   const handleClick=(id)=>{
 
     const newBlogs=blogs.filter(blog=>blog.id!==id);
     setBlogs(newBlogs);

   }
  

   return (
      <div className="home">
         <BlogList bloglar={blogs} baslik="Tüm yazılar" handleClick={handleClick}/>
         <br/>
      <button onClick={()=>setIsim('zoro')}>Değiştir</button>
      </div>
   );
}
export default Home;
