         yazar:"zorro",
         id:2
      },
      {
         ad:"İleri Seviye c#",
         aciklama:"lorem ipsum",
         yazar:"sanji",
         id:3
      },
      {
         ad:"İleri Seviye f#",
         aciklama:"lorem ipsum",
         yazar:"aliveli",
         id:4
      }

   ])
 
   const handleClick=(id)=>{
 
     console.log("silindi!");

   }
  

   return (
      <div className="home">
         <BlogList bloglar={blogs} baslik="Tüm yazılar" handleClick={handleClick}/>
         <br/>
         <BlogList bloglar={blogs.filter((blog)=>blog.yazar=='luffy')} baslik="seçkin yazarların yazıları"/>
      </div>
   );
}
export default Home;
