import { useState,useEffect } from "react";
import BlogList  from "./BlogList";
const Home = () => {
   
  const [blogs,setBlogs] =useState(null)
  const [yukleniyor,setYukleniyor]=useState(true)
 

    useEffect(()=>{
        fetch('http://localhost:8000/yazilar1')
        .then(res=>{
           if(!res.ok) throw Error('veriler çekilemedi!')
           return res.json(); 
        }).then(data=>{
            setBlogs(data);
            setYukleniyor(false);
        })
        .catch(err=>{
         console.log(err.message);
        })
    },[])


   const handleClick=(id)=>{
 
     const newBlogs=blogs.filter(blog=>blog.id!==id);
     setBlogs(newBlogs);

   }
  

   return (
      <div className="home">
         {yukleniyor && <div className="loading"> Yukleniyor..</div>}
                 {blogs&&<BlogList bloglar={blogs} baslik="Tüm yazılar" handleClick={handleClick}/> }
 
      </div>
   );
}
export default Home;
